      "C10": "[字符串]",
      "C11": "[字符串]"
    },
    "R_MMFF": "[字符串]",
    "R_UFF": "[字符串]",
    "Delta_R": "[字符串]"
  },
  "(2)": {
    "MMFF_largest_ring": "[字符串]",
    "MMFF_largest_DeltaE_span": "[字符串]",
    "UFF_corresponding_ring": "[字符串]",
    "UFF_DeltaE_span": "[字符串]"
  },
  "(3)": {
    "C9": {
      "N1k": "[数值]",
      "Neff": "[字符串]"
    },
    "C10": {
      "N1k": "[数值]",
      "Neff": "[字符串]"
    },
    "C11": {
      "N1k": "[数值]",
      "Neff": "[字符串]"
    }
  },
  "(4)": "[字符串]",
  "(5)": {
    "C9_path": "[字符串]",
    "C10_path": "[字符串]",
    "C11_path": "[字符串]"
  }
}

