{
  "A_MMFF": {
    "E_para_mmff": "[字符串]",
    "E_meta_mmff": "[字符串]",
    "E_ortho_mmff": "[字符串]",
    "DeltaE_total_MMFF": "[字符串]",
    "sigma_MMFF": "[字符串]"
  },
  "B_UFF": {
    "DeltaE_total_UFF": "[字符串]",
    "sigma_UFF": "[字符串]"
  },
  "C_triangle_identity_MMFF": "[字符串]",
  "D_fallback_basic_embedding": {
    "触发条件": "[字符串]",
    "本题执行情况": "[字符串]"
  },
  "E_neutral_vs_anion_MMFF": {
    "DeltaE_neutral_minus_anion": {
      "para": "[字符串]",
      "meta": "[字符串]",
      "ortho": "[字符串]"
    },
    "稳定性影响": "[字符串]"
  },
  "F_SMILES_for_verification": {
    "neutral": {
      "para": "[字符串]",
      "meta": "[字符串]",
      "ortho": "[字符串]"
    },
    "anion": {
      "para": "[数学方程字符串]",
      "meta": "[数学方程字符串]",
      "ortho": "[数学方程字符串]"
    }
  }
}

