现在请仅输出严格 JSON，必须完全匹配以下结构（键名、层级、字段齐全），所有数值用数字表示（保留2-3位小数），布尔用 true/false，字符串填写说明文字。不要输出任何解释文字或前后缀，也不要多余字段；若无法计算某值请给出可解析的近似值。

{
  "1": {
    "M_max": "[字符串]",
    "controlling_criterion": "[字符串]"
  },
  "2": {
    "T1_star": "[字符串]",
    "T2_star": "[字符串]",
    "tau1_star": "[字符串]",
    "tau2_star": "[字符串]",
    "SF_steel": "[字符串]",
    "SF_al": "[字符串]"
  },
  "3": {
    "k_eq": "[字符串]",
    "phi_at_R1": "[字符串]",
    "check": "[字符串]"
  },
  "4": {
    "torque_capacity_comparison": {
      "M_phi": "[字符串]",
      "M_steel": "[字符串]",
      "M_al": "[字符串]"
    },
    "shear_distribution_at_R1": {
      "steel_solid_rod_[r_mm, tau_MPa]": [
        "[2个字符串的数组]"
      ],
      "aluminum_tube_wall_[r_mm, tau_MPa]": [
        "[2个字符串的数组]"
      ]
    }
  },
  "5": {
    "given_data_numeric": {
      "J1": "[字符串]",
      "J2": "[字符串]",
      "k1": "[字符串]",
      "k2": "[字符串]",
      "k_eq": "[字符串]",
      "T1_unit": "[字符串]",
      "T2_unit": "[字符串]",
      "tau1_unit": "[字符串]",
      "tau2_unit": "[字符串]"
    },
    "results_reference_only": {
      "R1": "[字符串]",
      "R2": "[字符串]",
      "R3": "[字符串]",
      "R4": "[字符串]"
    }
  }
}

