          "d_A": 2.70
        },
        {
          "2theta_deg": 36.0,
          "intensity_obs": 90,
          "d_A": 2.49
        },
        {
          "2theta_deg": 37.9,
          "intensity_obs": 70,
          "d_A": 2.37
        }
      ]
    },
    "BAs": {
      "matched_peak_count": 7,
      "unique_marker_peaks": [
        {
          "2theta_deg": 27.2,
