现在请仅输出严格 JSON，必须完全匹配以下结构（键名、层级、字段齐全），所有数值用数字表示（保留2-3位小数），布尔用 true/false，字符串填写说明文字。不要输出任何解释文字或前后缀，也不要多余字段；若无法计算某值请给出可解析的近似值。

{
  "A": {
    "Bx": "[字符串]",
    "By": "[字符串]",
    "Bz": "[字符串]",
    "abs_B": "[字符串]"
  },
  "B": {
    "table_abs_B_T": [
      {
        "a_m": "[数值]",
        "abs_B": "[字符串]"
      }
    ],
    "scaling_conclusion": "[字符串]"
  },
  "C": {
    "direction_consistency": "[字符串]",
    "at_r_tst": {
      "L1_B": {
        "vector": "[字符串]",
        "direction": "[字符串]"
      },
      "L2_B": {
        "vector": "[字符串]",
        "direction": "[字符串]"
      }
    }
  },
  "D": {
    "ratio_Bx": "[字符串]",
    "ratio_Bz": "[字符串]",
    "ratio_abs_B": "[字符串]",
    "linearity_conclusion": "[字符串]"
  },
  "E": {
    "B_new_components": {
      "Bx": "[字符串]",
      "By": "[字符串]",
      "Bz": "[字符串]"
    },
    "abs_B_new": "[字符串]",
    "relative_change": "[字符串]",
    "angle_between_old_new": "[字符串]"
  },
  "F": {
    "summary": {
      "distance_scaling": "[字符串]",
      "direction_rule_vs_cross": "[字符串]",
      "current_linearity": "[字符串]",
      "superposition_with_L3": "[字符串]"
    },
    "consistency_flags": "[字符串]"
  }
}

