      "promoter_TSS": "[数值]",
      "enhancer": "[数值]",
      "insulator_CTCF": "[数值]",
      "repeat_pericentromeric": "[数值]",
      "gene_body": "[数值]",
      "heterochromatin": "[数值]"
    }
  },
  "II.d": {
    "pearson_r_fraction_scale": "[数值]",
    "rmse_fraction_scale": "[数值]",
    "reference_log_scale": {
      "pearson_r": "[数值]",
      "rmse": "[数值]"
    }
  },
  "II.e": {
    "final_high_confidence_disappearing_count": "[数值]"
  },
  "II.f": {
    "threshold_summary": [
      {
        "name": "[字符串]",
        "value": "[数值]",
        "threshold": "[字符串]",
        "pass": "[布尔值]"
      }
    ],
    "overall_pass": "[布尔值]"
  }
}

