      "S_sel_selection_marker_change": "[字符串]",
      "B_compat_compatible": "[布尔值]",
      "B_conflict_origin_conflict": "[布尔值]"
    }
  },
  "2": {
    "difficulty_scores": {
      "D10_main": "[数值]",
      "D100_main": "[数值]",
      "category": "[字符串]"
    },
    "bottlenecks": "[3个字符串的数组]",
    "assumptions": "[4个字符串的数组]"
  },
  "3": {
    "strategy_profiles": [
      {
        "name": "[字符串]",
