      },
      "delta_vs_main": {
        "Δ_D100_main_minus_ctrl": "[数值]",
        "explanation_brief": "[字符串]"
      }
    }
  },
  "5": {
    "cross_validation": {
      "visual_key_points": "[3个字符串的数组]",
      "difficulty_driver_main": "[字符串]",
      "cross_check_pass": "[布尔值]",
      "mismatches": []
    }
  },
  "6": {
    "numeric_summary_symbolic_only": {
      "D10_main": "[字符串]",
      "D100_main": "[字符串]",
      "D10_ctrl": "[字符串]",
      "D100_ctrl": "[字符串]",
      "Δ": "[字符串]",
      "R1": "[字符串]",
      "ΔL": "[字符串]",
      "B_compat": "[字符串]",
      "B_conflict": "[字符串]",
      "S_sel": "[字符串]"
    }
  }
}

