现在请仅输出严格 JSON，必须完全匹配以下结构（键名、层级、字段齐全），所有数值用数字表示（保留2-3位小数），布尔用 true/false，字符串填写说明文字。不要输出任何解释文字或前后缀，也不要多余字段；若无法计算某值请给出可解析的近似值。

{
  "1": {
    "wavelength_used": "[字符串]",
    "RMSE_2θ_deg_per_candidate": {
      "Al": "[数值]",
      "Au": "[数值]",
      "Ag": "[数值]",
      "Cu": "[数值]",
      "Ni": "[数值]"
    },
    "best_match_M1": "[字符串]",
    "space_group": "[字符串]",
    "MP_id": "[字符串]",
    "reference_vs_refined_consistency": "[字符串]"
  },
  "2": {
    "assignments_hkl": "[5个字符串的数组]",
    "FCC_extinction_check": "[字符串]",
    "sin2theta_vs_N_linearity_R2": "[数值]"
  },
  "3": {
    "a_i_A": {
      "111": "[数值]",
      "200": "[数值]",
      "220": "[数值]",
      "311": "[数值]",
      "222": "[数值]"
    },
    "a_mean_A": "[数值]",
    "a_standard_uncertainty_A": "[数值]",
    "a_refined_A": "[数值]",
    "RMSE_2θ_deg": "[数值]",
    "chi2_reduced": "[数值]",
    "difference_sources": "[字符串]",
    "Ka_doublet_sensitivity": {
      "a_with_Kα2_A": "[数值]",
      "delta_a_A": "[数值]",
      "relative_change_percent": "[数值]"
    }
  },
  "4": {
    "final_matching_score": {
      "RMSE_2θ_deg": "[字符串]",
      "max_abs_delta_2θ_deg": "[数值]"
    },
    "acceptance_decision": "[字符串]"
  }
}

