现在请仅输出严格 JSON，必须完全匹配以下结构（键名、层级、字段齐全），所有数值用数字表示（保留2-3位小数），布尔用 true/false，字符串填写说明文字。不要输出任何解释文字或前后缀，也不要多余字段；若无法计算某值请给出可解析的近似值。

{
  "A": {
    "valid": "[布尔值]",
    "length": "[字符串]",
    "gc_content": "[数值]",
    "base_composition": {
      "G": "[数值]",
      "C": "[数值]",
      "A": "[数值]",
      "U": "[数值]"
    },
    "has_3prime_CCA": "[布尔值]",
    "in_tRNA_range": "[布尔值]",
    "in_mRNA_feature_range": "[布尔值]",
    "reason": "[字符串]"
  },
  "B": {
    "run_A_total_pairs": "[数值]",
    "run_A_stem_regions": [
      {
        "start": "[数值]",
        "end": "[数值]",
        "length": "[数值]"
      }
    ],
    "run_B_total_pairs": "[数值]",
    "run_B_stem_regions": [
      {
        "start": "[数值]",
        "end": "[数值]",
        "length": "[数值]"
      }
    ],
    "diff_percent_vs_A": "[字符串]",
    "robust_by_threshold": "[布尔值]",
    "max_stem_length_consistent": "[布尔值]"
  },
  "C": {
    "stem_count": "[数值]",
    "pseudoknot_count": "[数值]",
    "long_range_interactions": "[数值]",
    "complexity_score": "[数值]",
    "has_tertiary": "[布尔值]",
    "concordance": "[字符串]"
  },
  "D": {
    "initial_label": "[字符串]",
    "confidence": "[数值]",
    "feature_mapping": "[2个字符串的数组]"
  },
  "E": {
    "catalytic_score": "[数值]",
    "ribozyme_threshold": "[数值]",
    "is_ribozyme": "[布尔值]",
    "key_explanation": "[字符串]"
  },
  "F": {
    "secondary_label": "[字符串]",
    "secondary_consistent_with_initial": "[布尔值]",
    "consistent_with_sequence_function_constraints": "[布尔值]",
    "discrepancy_reason": "[字符串]",
    "override_rule_triggered": "[布尔值]",
    "final_label": "[字符串]"
  }
}

