messages.content:
现在请仅输出严格 JSON，必须完全匹配以下结构（键名、层级、字段齐全），所有数值用数字表示（保留2-3位小数），布尔用 true/false，字符串填写说明文字。不要输出任何解释文字或前后缀，也不要多余字段；若无法计算某值请给出可解析的近似值。

{
  "(1-1)": {
    "态数通式": "[数学方程字符串]",
    "I_1_2_态数": "[字符串]",
    "I_1_2_m_值": "[2个字符串的数组]"
  },
  "(1-2)": {
    "n_个自旋_1_2_总基态数目": "[字符串]",
    "n_等于_2": "[字符串]",
    "n_等于_3": "[字符串]"
  },
  "(1-3)": {
    "基底": "[4个字符串的数组]",
    "量子数": [
      {
        "态": "[字符串]",
        "m1": "[字符串]",
        "m2": "[字符串]",
        "M": "[字符串]"
      }
    ]
  },
  "(2-1)": {
    "Beff1": "[字符串]",
    "omega1": "[字符串]",
    "nu1": "[字符串]",
    "DeltaE1_表达式": "[数学方程字符串]",
    "DeltaE1_数量级": "[字符串]"
  },
  "(2-2)": {
    "Beff2": "[字符串]",
    "omega2": "[字符串]",
    "nu2": "[字符串]",
    "DeltaE2_表达式": "[数学方程字符串]",
    "DeltaE2_数量级": "[字符串]"
  },
  "(2-3)": {
    "Delta_nu_表达式": "[字符串]",
    "用_ppm_估算": "[字符串]",
    "Delta_nu_数量级": "[字符串]"
  },
  "(3-1)": {
    "比较": "[字符串]",
    "数量级差": "[字符串]"
  },
  "(3-2)": {
    "DeltaE_dip_表达式": "[字符串]",
    "DeltaE_dip_数量级": "[字符串]",
    "比较": "[字符串]",
    "数量级差": "[字符串]"
  },
  "(3-3)": {
    "液体高分辨_NMR": "[字符串]",
    "固体或各向异性环境": "[字符串]"
  },
  "(4-1)": {
    "一般表达式": "[数学方程字符串]",
    "各态能量": {
      "E_alpha_alpha": "[数学方程字符串]",
      "E_alpha_beta": "[字符串]",
      "E_beta_alpha": "[数学方程字符串]",
      "E_beta_beta": "[数学方程字符串]"
    }
  },
  "(4-2)": {
    "alpha_alpha_beta_beta_仍为本征态原因": "[数学方程字符串]",
    "只在_alpha_beta_beta_alpha_子空间有非对角元原因": "[字符串]"
  },
  "(4-3)": {
    "本征值通式": "[数学方程字符串]",
    "本征态形式": {
      "对应_λ_plus": "[数学方程字符串]",
      "对应_λ_minus": "[数学方程字符串]"
    }
  },
  "(5-1)": {
    "允许跃迁_初末态": [
      {
        "跃迁1": "[字符串]"
      }
    ],
    "对应频率_符号形式": {
      "自旋1相关跃迁": "[字符串]",
      "自旋2相关跃迁": "[字符串]"
    },
    "谱线数与频率分布": {
      "互不简并谱线数": "[字符串]",
      "对应频率": "[字符串]"
    }
  },
  "(5-2)": {
    "能量差_符号形式": {
      "DeltaE_01": "[字符串]",
      "DeltaE_02": "[字符串]",
      "DeltaE_13": "[字符串]",
      "DeltaE_23": "[字符串]"
    },
    "对应频率_符号形式": {
      "nu_01": "[字符串]",
      "nu_02": "[字符串]",
      "nu_13": "[字符串]",
      "nu_23": "[字符串]"
    },
    "与无耦合情况的比较": {
      "主频率尺度": "[字符串]",
      "偶极引入的微小偏移": "[字符串]",
      "数量级差": "[字符串]",
      "是否可分辨": "[字符串]"
    }
  },
  "(6-1)": {
    "态数_公式应用": "[字符串]",
    "耦合对数目的影响": "[字符串]",
    "能量变化": "[字符串]"
  },
  "(6-2)": {
    "跃迁数目": "[字符串]",
    "与一般情况的一致性": "[字符串]"
  },
  "(6-3)": {
    "一般态数公式": "[字符串]",
    "两自旋_强场弱耦合能级与谱线": {
      "能级结构": "[数学方程字符串]",
      "谱线数目": "[字符串]"
    },
    "忽略偶极耦合的条件": {
      "频率尺度比较": "[字符串]",
      "实验条件": "[字符串]"
    }
  }
}

{
  "(1-1)": {
    "态数通式": "2I + 1",
    "I_1_2_态数": "2",
    "I_1_2_m_值": ["+1/2", "-1/2"]
  },
  "(1-2)": {
    "n_个自旋_1_2_总基态数目": "2^n",
    "n_等于_2": "4",
    "n_等于_3": "8"
  },
  "(1-3)": {
    "基底": ["|αα>", "|αβ>", "|βα>", "|ββ>"],
    "量子数": [
      {
        "态": "|αα>",
        "m1": "+1/2",
        "m2": "+1/2",
        "M": "+1"
      },
      {
        "态": "|αβ>",
        "m1": "+1/2",
        "m2": "-1/2",
        "M": "0"
      },
      {
        "态": "|βα>",
        "m1": "-1/2",
        "m2": "+1/2",
        "M": "0"
      },
      {
        "态": "|ββ>",
        "m1": "-1/2",
        "m2": "-1/2",
        "M": "-1"
      }
    ]
  },
  "(2-1)": {
    "Beff1": "B₀·(1 - δ₁×10⁻⁶)",
    "omega1": "γ·B₀·(1 - δ₁×10⁻⁶)",
    "nu1": "γ·B₀·(1 - δ₁×10⁻⁶)/(2π)",
    "DeltaE1_表达式": "ħ·γ·B₀·(1 - δ₁×10⁻⁶)",
    "DeltaE1_数量级": "2.8×10⁻²⁶ J"
  },
  "(2-2)": {
    "Beff2": "B₀·(1 - δ₂×10⁻⁶)",
    "omega2": "γ·B₀·(1 - δ₂×10⁻⁶)",
    "nu2": "γ·B₀·(1 - δ₂×10⁻⁶)/(2π)",
    "DeltaE2_表达式": "ħ·γ·B₀·(1 - δ₂×10⁻⁶)",
    "DeltaE2_数量级": "2.8×10⁻²⁶ J"
  },
  "(2-3)": {
    "Delta_nu_表达式": "γ·B₀·|δ₂ - δ₁|×10⁻⁶/(2π)",
    "用_ppm_估算": "440 Hz",
    "Delta_nu_数量级": "10² Hz"
  },
  "(3-1)": {
    "比较": "J_dip ≈ 0.11 Hz 与 Δν ≈ 440 Hz",
    "数量级差": "3个数量级"
  },
  "(3-2)": {
    "DeltaE_dip_表达式": "ħ·2π·J_dip",
    "DeltaE_dip_数量级": "7.3×10⁻³⁵ J",
    "比较": "ΔE_dip 与 ΔE₁ 比较",
    "数量级差": "9个数量级"
  },
  "(3-3)": {
    "液体高分辨_NMR": "可以忽略该偶极耦合对谱线位置与裂分的影响",
    "固体或各向异性环境": "在偶极耦合与化学位移差或J耦合可比时，需要考虑偶极耦合对谱线线宽或细微裂分的贡献"
  },
  "(4-1)": {
    "一般表达式": "E(m₁,m₂) = -ħ·(ω₁m₁ + ω₂m₂)",
    "各态能量": {
      "E_alpha_alpha": "-ħ·(ω₁ + ω₂)/2",
      "E_alpha_beta": "-ħ·(ω₁ - ω₂)/2",
      "E_beta_alpha": "-ħ·(-ω₁ + ω₂)/2",
      "E_beta_beta": "ħ·(ω₁ + ω₂)/2"
    }
  },
  "(4-2)": {
    "alpha_alpha_beta_beta_仍为本征态原因": "总磁量子数M守恒",
    "只在_alpha_beta_beta_alpha_子空间有非对角元原因": "这两个态具有相同的M = 0，而|αα>和|ββ>具有不同的M值"
  },
  "(4-3)": {
    "本征值通式": "(a + c)/2 ± √[(a - c)²/4 + b²]",
    "本征态形式": {
      "对应_λ_plus": "|αβ>与|βα>的对称型组合",
      "对应_λ_minus": "|αβ>与|βα>的反对称型组合"
    }
  },
  "(5-1)": {
    "允许跃迁_初末态": [
      {
        "跃迁1": "|αα> → |αβ>"
      },
      {
        "跃迁1": "|αα> → |βα>"
      },
      {
        "跃迁1": "|αβ> → |ββ>"
      },
      {
        "跃迁1": "|βα> → |ββ>"
      }
    ],
    "对应频率_符号形式": {
      "自旋1相关跃迁": "ħ·ω₁",
      "自旋2相关跃迁": "ħ·ω₂"
    },
    "谱线数与频率分布": {
      "互不简并谱线数": "2",
      "对应频率": "ν₁和ν₂附近"
    }
  },
  "(5-2)": {
    "能量差_符号形式": {
      "DeltaE_01": "|E₁ - E₀|",
      "DeltaE_02": "|E₂ - E₀|",
      "DeltaE_13": "|E₃ - E₁|",
      "DeltaE_23": "|E₃ - E₂|"
    },
    "对应频率_符号形式": {
      "nu_01": "|E₁ - E₀|/h",
      "nu_02": "|E₂ - E₀|/h",
      "nu_13": "|E₃ - E₁|/h",
      "nu_23": "|E₃ - E₂|/h"
    },
    "与无耦合情况的比较": {
      "主频率尺度": "ν₁和ν₂",
      "偶极引入的微小偏移": "约0.11 Hz",
      "数量级差": "3个数量级",
      "是否可分辨": "不能产生可分辨的谱线裂分"
    }
  },
  "(6-1)": {
    "态数_公式应用": "4",
    "耦合对数目的影响": "态的数目不变",
    "能量变化": "能量改变"
  },
  "(6-2)": {
    "跃迁数目": "4",
    "与一般情况的一致性": "一致"
  },
  "(6-3)": {
    "一般态数公式": "2^n",
    "两自旋_强场弱耦合能级与谱线": {
      "能级结构": "4个能级",
      "谱线数目": "2条谱线"
    },
    "忽略偶极耦合的条件": {
      "频率尺度比较": "偶极耦合与化学位移差或J耦合相比可忽略（相差3个数量级以上）",
      "实验条件": "高分辨液体NMR条件"
    }
  }
}

